// Import
import React, { useEffect, useState } from "react";
import { Link, useOutletContext,useParams } from "react-router-dom";

// Functional Component
const Department = () => {
    const { departmentState: [department, setDepartment] } = useOutletContext();

        // fetchDepartmentData
        useEffect(() => {
            async function fetchDepartmentData () {
                try {
                    // Fetch the data;
                    const response = await fetch ("https://dal-recipe-back.onrender.com/api/department",{
                        headers: {
                            'Content-Type' : 'application/json'
                        }
                    })
                    // Translate the promise data;
                    // console.log("This is the Department response:", response)
                    const departmentData = await response.json();
                    console.log("This is the ranslated Department JSON response:", departmentData.data);

                    // Save the data to the State;
                    setDepartmentData(departmentData.data)
                } catch (error) {
                    console.log("Error fetching Department Data:" )
                    console.log(error);
                }
            }
            fetchDepartmentData();
        }, [])

    return(
        <div>
            <p id="title-text">Recipes & Cooking Ideas</p>

            <div class="department-box">
                <div id="department-box-breakfast">
                    {
                        department.map((department, idx) => {
                            return <div key={idx}>
                                <p id="department-box-text">Name of Department: {department.departmentName}</p>
                            </div>
                        })
                    }
                </div>
                <div id="department-box-entrees"></div>
                <div id="department-box-desserts"></div>
                <div id="department-box-sides"></div>
            </div>
        </div>
    )
};

// Export
export default Department;

