// Import
import React, { useEffect, useState } from "react";
import { Link, useOutletContext, useParams } from "react-router-dom";

// Functional Component
const Department = () => {
    const { departmentState: [department, setDepartment] } = useOutletContext();

    return (

        department ? department.map((indivDepartment, idx) => {
            return(

                <div key={idx} id="candy-container">
                    <div>
                        <button id="candy-button">
                            <Link to={`/candy/${indivDepartment.departmentId}`}>View Item</Link>
                        </button>
                    </div>

                    <h2 id="candy-name">{indivDepartment.departmentName}</h2>
                    {/* <div>
                        <button id="candy-button">
                            <Link to={`/candy/${indivCandy.candyId}`}>View Item</Link>
                        </button>
                    </div> */}
                </div>
            )
        }) : "No Candy to View" 
    )
};
// Export
export default Department;


